mapper.select:
SELECT 

FROM reserve_field a

WHERE a.id = #{id}

SELECT 

FROM reserve_field a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')



			AND venue_id = #{reserveVenue.id}





		ORDER BY ${page.orderBy}


		ORDER BY a.name asc

SELECT 

FROM reserve_field a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.tenant_id = #{tenantId}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_field SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
